import Button from "./Button";
import styles from "./AddUser.module.css";
import { useState } from "react";

const AddUser = (props) => {
  const [userInput, setUserInput] = useState({
    userName: "홍길동",
    age: 20,
  });

  const userInputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
        return{
            ...prevInput,
            [input]: value,
        };
    });
  };

  return (
    <form className={styles.input} onSubmit={props.onSubmitHandler}>
      <label htmlFor="userName" className={styles.input.label}>
        UserName
      </label>
      <input
        type="text"
        id="userName"
        value={userInput.userName}
        onChange={(event) =>
          userInputChangeHandler("userName", event.target.value)
        }
      ></input>
      <label htmlFor="age">Age (Years)</label>
      <input
        type="number"
        id="age"
        value={userInput.age}
        onChange={(event) => userInputChangeHandler("age", event.target.value)}
      ></input>
      <Button/>
    </form>
  );
};

export default AddUser;
