import Button from "./Button";
import styles from "./AddUser.module.css";

import Card from "./UI/Card";
import { useState } from "react";

const AddUser = (props) => {
  const [userInput, setUserInput] = useState({
    userName: null,
    age: null,
  });

  const userInputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
      return{
        ...prevInput,
        [input]: value,
      };
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(userInput.userName + ", " + userInput.age);
    setUserInput({
      userName: null,
      age: null,
    });
    //props.onAddUserCard(userInput);
  };

  return (
    <Card className={styles.input}>
      <form onSubmit={submitHandler}>
        <label htmlFor="userName" className={styles.input.label}>
          UserName
        </label>
        <input
          type="text"
          id="userName"
          value={userInput.userName}
          onChange={(event) =>
            userInputChangeHandler("userName", event.target.value)
          }
        ></input>
        <label htmlFor="age">Age (Years)</label>
        <input
          type="number"
          id="age"
          value={userInput.age}
          onChange={(event) =>
            userInputChangeHandler("age", event.target.value)
          }
        ></input>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
