import Button from "./Button";
import styles from "./AddUser.module.css";
import { useState } from "react";

const AddUser = (props) => {

  const userInput = {
    userName: null,
    age: null,
  };

  const userInputChangeHandler = (input, value) => {
    userInput[input] = value;
  };

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(userInput);
    props.onAddUserCard(userInput);
  };

  return (
    <form className={styles.input} onSubmit={submitHandler}>
      <label htmlFor="userName" className={styles.input.label}>
        UserName
      </label>
      <input
        type="text"
        id="userName"
        value={userInput.userName}
        onChange={(event) =>
          userInputChangeHandler("userName", event.target.value)
        }
      ></input>
      <label htmlFor="age">Age (Years)</label>
      <input
        type="number"
        id="age"
        value={userInput.age}
        onChange={(event) => userInputChangeHandler("age", event.target.value)}
      ></input>
      <Button />
    </form>
  );
};

export default AddUser;
