import Button from "./Button";
import styles from "./AddUser.module.css";

import Card from "./UI/Card";
import { useState } from "react";

const AddUser = (props) => {
  const [enteredUserName, setEnteredUserName] = useState('');
  const [enteredAge, setEnteredAge] = useState('');

  const userNameChagneHandler = (value) => {
    setEnteredUserName(value);
  };

  const ageChangeHandler = (value) => {
    setEnteredAge(value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if(enteredUserName.trim().length === 0 || enteredAge.trim().length === 0){

    }
    console.log(enteredUserName + ", " + enteredAge);
    setEnteredUserName('');
    setEnteredAge('');
    props.onAddUserCard(enteredUserName, enteredAge);
  };

  return (
    <Card className={styles.input}>
      <form onSubmit={submitHandler}>
        <label htmlFor="userName" className={styles.input.label}>
          UserName
        </label>
        <input
          type="text"
          id="userName"
          value={enteredUserName}
          onChange={(event) =>
            userNameChagneHandler(event.target.value)
          }
        ></input>
        <label htmlFor="age">Age (Years)</label>
        <input
          type="number"
          id="age"
          value={enteredAge}
          onChange={(event) =>
            ageChangeHandler(event.target.value)
          }
        ></input>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
