import React, { useState } from "react";
import Card from "./UI/Card";
import Button from "./UI/Button";
import ErrorModal from "./UI/ErrorModal";
import styles from "./AddUser.module.css";

const AddUser = (props) => {
  const [enteredUserName, setEnteredUserName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [error, setError] = useState();

  const userNameChagneHandler = (value) => {
    setEnteredUserName(value);
  };

  const ageChangeHandler = (value) => {
    setEnteredAge(value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    // Data Validation check
    if (enteredUserName.trim().length === 0 || enteredAge.trim().length === 0) {
      setError({
        title: "Invalid Input",
        message: "Plesas enter a valid name and age (non-empty values).",
      });
      return;
    }
    if (+enteredAge < 1) {
      setError({
        title: "Invalid age",
        message: "Plesas enter a valid age (> 0).",
      });
      return;
    }
    console.log(enteredUserName + ", " + enteredAge);
    props.onAddUser(enteredUserName, enteredAge);
    setEnteredUserName("");
    setEnteredAge("");
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <React.Fragment> 
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
        />
      )}
      <Card className={styles.input}>
        <form onSubmit={submitHandler}>
          <label htmlFor="userName" className={styles.input.label}>
            UserName
          </label>
          <input
            type="text"
            id="userName"
            value={enteredUserName}
            onChange={(event) => userNameChagneHandler(event.target.value)}
          ></input>
          <label htmlFor="age">Age (Years)</label>
          <input
            type="number"
            id="age"
            value={enteredAge}
            onChange={(event) => ageChangeHandler(event.target.value)}
          ></input>
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </React.Fragment>
  );
};

export default AddUser;
