import Button from "./Button";
import styles from "./AddUser.module.css";

import Card from "./UI/Card"

const AddUser = (props) => {

  const userInput = [];

  const userInputChangeHandler = (input, value) => {
    userInput[input] = value;
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onAddUserCard(userInput);
  };

  return (
    <Card>
    <form className={styles.input} onSubmit={submitHandler}>
      <label htmlFor="userName" className={styles.input.label}>
        UserName
      </label>
      <input
        type="text"
        id="userName"
        onChange={(event) =>
          userInputChangeHandler("userName", event.target.value)
        }
      ></input>
      <label htmlFor="age">Age (Years)</label>
      <input
        type="number"
        id="age"
        onChange={(event) => userInputChangeHandler("age", event.target.value)}
      ></input>
      <Button />
    </form>
    </Card>
  );
};

export default AddUser;
